<?php
declare(strict_types=1);
final class OrderStatusHistoryController { private OrderStatusHistoryService $service; public function __construct(OrderStatusHistoryService $service) { $this->service = $service; } public function list(int $tenantId, ?int $limit = null, ?int $offset = null, array $filters = [], string $orderBy = 'id', string $orderDir = 'DESC', string $lang = 'ar'): array { return ['items' => $this->service->list($tenantId, $limit, $offset, $filters, $orderBy, $orderDir, $lang), 'total' => $this->service->count($tenantId, $filters)]; } public function get(int $tenantId, int $id, string $lang = 'ar'): ?array { return $this->service->get($tenantId, $id, $lang); } public function create(int $tenantId, array $data): int { return $this->service->create($tenantId, $data); } }
